style_head:
.grecaptcha-badge {
    display: none !important;
}

.b-data-center-view__contact li {
	padding: 0 0 10px 0;
}

.table-responsive {
	padding: 3px;
}
.table-text {
	margin-bottom: 20px;
	border: 3px solid white;
	outline: 3px solid #ffd300;
	background: white !important;
}
.table-text th {
	padding: 7px;
	background: #ffd300;
}
.table-text td {
	padding: 7px;
	border: 3px solid white;
}
.table-text tbody tr:nth-child(even) {
	background: #f8f8f8 !important;
}
